function getLinktext(name, ids) {
  var linktext = "";

  for (var id in ids) {
    if (id != "name") {
      linktext += "<a target=\"_blank\" href=\"http://identifiers.org/".concat(id, "/").concat(ids[id], "\">").concat(name, "</a>");
      break;
    }
  }

  return linktext == "" ? name : linktext;
}

